
public class Patterneig {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for(int i=1;i<=5;i++)//print rows
    {
    	for(int j=1;j<=i;j++)//print no of space
    	{
    		System.out.print(" ");
    	}
    	for(int j=1;j<=i;j++)//print i no of stars
    	{
    	System.out.print("*");
    }
    System.out.println();
	}

}}
